<script lang="ts">
import DefaultButton from "./building_parts/DefaultButton.vue";
import DefaultInput from "./building_parts/DefaultInput.vue";
import { decrypt } from "@metamask/browser-passworder";
import { Vault } from "../vulture_backend/wallets/vultureWallet";
import { PropType } from 'vue';
export default {
  name: "UnlockWallet",
  components: {
    DefaultButton,
    DefaultInput,
  },
  data() {
      return {
          password: '',
          wrongPassword: false,
          wrongPasswordAmount: 0,
      }
  },
  props: {
      encryptedVault: String,
  },
  methods: {
    setPassword(password: any) {
        this.password = password;
    },
    attemptUnlock() {
        decrypt(this.password, this.encryptedVault).then((result) => {
            this.$emit("decrypted-vault", result);
        }).catch((error) => {
            console.log(error);
            this.wrongPassword = true;
            this.wrongPasswordAmount++;
        });
    }
  }
};
</script>

