<script lang="ts">
import { PropType, reactive, ref } from 'vue';
import { VultureWallet } from '@/vulture_backend/wallets/vultureWallet';

import DefaultButton from "./building_parts/DefaultButton.vue";
import MinimalInput from "./building_parts/MinimalInput.vue";
import TokenModule from "./TokenModule.vue";
import { VultureMessage } from '@/vulture_backend/vultureMessage';

export default {
  name: "WalletTab",
  components: {
    DefaultButton,
    MinimalInput,
    TokenModule
  },
  props: {
      vultureWallet: {
      type: Object as PropType<VultureWallet>,
      required: true,
    },

  },
  setup(props: any, context: any) {
    let currentTab = ref("Assets");

    function addToken(tokenType: string) {
      //(props.vultureWallet as VultureWallet).test();
      context.emit("add-custom-token", tokenType);
    }
    function setTab(tab: string) {
      currentTab.value = tab;
    }
    function openToken(arrayIndexOfToken: number, tokenType: string) {
      context.emit("token-view-modal", {index: arrayIndexOfToken, type: tokenType});
    }
    return {
      currentTab,

      openToken: openToken,
      addToken: addToken,
      setTab: setTab
    }
  }
};
</script>

