<script lang="ts">
import DefaultButton from "../building_parts/DefaultButton.vue";
import DefaultInput from "../building_parts/DefaultInput.vue";
import SeedWordBox from "../webAppSpecific/SeedWordBox.vue";
import SeedPhraseInput from "./SeedPhraseInput.vue";
import { generateMnemonic } from 'bip39';
import { createVault, Vault, createNewAccount, Network, WalletType, deleteWallet, NetworkType, DefaultNetworks } from "../../vulture_backend/wallets/IvultureWallet"
import { ref } from 'vue';

import TransportWebUSB from "@ledgerhq/hw-transport-webusb";
import Polkadot from "@ledgerhq/hw-app-polkadot";



export default {
  name: "Onboarding",
  components: {
    SeedPhraseInput,
    DefaultButton,
    DefaultInput,
    SeedWordBox,
  },
  props: {
  },
  data() {
    return {
        onboardingState: "start", // | start, importSeed, createSeed, createPassword, finished | are used.
        hasValidSeed: false,
        seedPhrase: '',

        password: '',
        verifyPassword: '',
        isValidPassword: false
    }
  },
  setup() {

    let ledgerConnectionStatus = ref('none');

    let ledgerTransport;

    let seed = ref(generateMnemonic(256));
    let generatedSeed = ref(seed.value.split(' '));

    //Ledger connection will be abstracted to be part of the vulture_backend once
    //the proof-of-concept has been verified.  
    function connectLedger() {
        console.log("Attemption Ledger Connection through WebUSB.");
        TransportWebUSB.create().then((transport) => {
            ledgerTransport = transport;
            /*
            const polkadot = new Polkadot(transport);
            
            polkadot.getAddress("m/44'/643'0'/0/0", false).then((result) => {
                console.log(result);
            }).catch((error) => {
                console.log(error);
                ledgerConnectionStatus.value = "polkadot app not detected";
            });
             */
            
        }).catch((error) => {
            if(error.message == "No device selected.") {
                console.log(error);
                ledgerConnectionStatus.value = error.message;
            }
        })
    }

    return {
        ledgerConnectionStatus,
        generatedSeed,
        connectLedger: connectLedger
    }
  },
  methods: {
    setSeed(seed: string) {
        this.seedPhrase = seed;
        this.hasValidSeed = true;
    },
    setPassword(password: string, isPassword: string) {
        if(isPassword) {
            this.password = password;
        }else {
            this.verifyPassword = password;
        }
        if(Array.from(this.password).length > 10 && this.password === this.verifyPassword) {
            this.isValidPassword = true;
        } else {
            this.isValidPassword = false;
        }
    },
    createWallet() {
        this.onboardingState = 'createSeed';
    },
    importSeed() {
        this.onboardingState = 'importSeed';
    },
    hardwareWallet() {
        this.onboardingState = 'hardwareWallet';
    },

    next() {
        if(this.onboardingState == 'importSeed' && this.hasValidSeed) {
            this.onboardingState = 'createPassword';
        }
        if(this.onboardingState == 'createSeed') {
            this.onboardingState = 'verifySeed';
        }
        if(this.onboardingState == 'verifySeed'  && this.hasValidSeed) {
            this.onboardingState = 'createPassword';
        }
        if(this.onboardingState == 'createPassword' && this.isValidPassword) {
            //Create a vault storing our SeedPhrase~!
            let vault: Vault = {
                seed: this.seedPhrase
            };

            //Create a vault.
            createVault(vault, this.password);

            //Create the initial wallet account.
            createNewAccount('First Account', WalletType.MnemonicPhrase, new DefaultNetworks().AlephZero);
            console.log("Succesfully Created Vulture Account!");
            this.onboardingState = 'finished';
        }
    },
    back() {
        if(this.onboardingState == 'importSeed') {
            this.onboardingState = 'start';
            this.seedPhrase = "";
            this.hasValidSeed = false;
        }
        if(this.onboardingState == 'createSeed') {
            this.onboardingState = 'start';
        }
        if(this.onboardingState == 'hardwareWallet') {
            this.onboardingState = 'start';
        }
        if(this.onboardingState == 'verifySeed') {
            this.hasValidSeed = false;
            this.seedPhrase = "";
            this.onboardingState = 'createSeed';
        }
    },
  }
};
</script>

